package day01;

public class OperEx4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			int a = 10;
			int b = 20;
			
			System.out.println(" a > b : " + (a > b));
			System.out.println(" a >= b : " + (a >= b));
			System.out.println(" a > b : " + (a < b));
			System.out.println(" a <= b : " + (a <= b));
			System.out.println(" a == b : " + (a == b));
			System.out.println(" a != b : " + (a != b));
			
		
	}

}
